import React, { useState } from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Modal from 'react-bootstrap/Modal';
import { deleteVideo } from '../services/allAPI';

function VideoCard({displayVideo}) {
    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const removeVideo = async(id) => {
    
    const respose = await deleteVideo(id);
    if (respose.status === "SUCCESS"){
      window.location.reload();
      }else{
        alert("Error");
        };
        console.log(respose);


  }
    return (
        <>


            <Card style={{ width: '16rem'  }} className='mb-5'>
                <Card.Img variant="top" height="300px" src={displayVideo.url} onClick={handleShow} />
                <Card.Body>
                    <div className='d-flex justify-content-between'> 
                     <h5 style={{ fontFamily: "-moz-initial" }}>{displayVideo.caption}</h5>
                        <Button variant="danger"className='ms-5' onClick={()=>removeVideo(displayVideo.id)}>
                            <i class="fa-solid fa-trash"></i>
                        </Button>
                    </div>
                </Card.Body>
            </Card>

            <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <iframe width="450px" height="500" src="https://www.youtube.com/embed/9JaBo8h67qo" title="Muthuchippi" frameborder="0" allow="accelerometer; autoplay; 
        clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowfullscreen></iframe>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Understood</Button>
        </Modal.Footer>
      </Modal>
        </>

    )
}

export default VideoCard
