import React, { useState } from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Modal from 'react-bootstrap/Modal';

function VideoCard({displayVideo}) {
    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
    return (
        <>


            <Card style={{ width: '100%',height:"250Px",  }}>
                <Card.Img variant="top" height="350px" width="100%" src={displayVideo.url} onClick={handleShow} />
                <Card.Body>
                    <Card.Title></Card.Title>

                    <div className='d-flex align-items-center justify-content-evenly'> 
                     <h5 style={{ fontFamily: "-moz-initial" }}>{displayVideo.caption}</h5>
                        <Button variant="danger" className='ms-5'>
                            <i class="fa-solid fa-trash"></i>
                        </Button>
                    </div>
                </Card.Body>
            </Card>

            <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <iframe width="40px" height="500" src="https://www.youtube.com/embed/9JaBo8h67qo" title="Muthuchippi" frameborder="0" allow="accelerometer; autoplay; 
        clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowfullscreen></iframe>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Understood</Button>
        </Modal.Footer>
      </Modal>
        </>

    )
}

export default VideoCard
