import React from 'react';
import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';

function Add() {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [video, setVideo] = useState({
        id:"",
        caption:"",
        
    })
    return (
        <>
            <div className='d-flex align-items-center gap-2 bg-none'>
    <h5 style={{fontFamily:"-moz-initial"}}>Upload New Video<i class="fa-solid fa-cloud-arrow-up ms-2" onClick={handleShow}></i></h5>
  </div>

            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title style={{ fontFamily: "-moz-initial", fontSize: "23px" }}>  <i class="fa-solid fa-film text-warning me-3"></i>Upload Video</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p style={{fontFamily:"-moz-initial"}}>Fill the details</p>
                    <Form className='border border-secondary p-3'>
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                            <Form.Control type="text" placeholder="Enter Video ID" />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                            <Form.Control type="text" placeholder="Enter Video Caption" />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                            <Form.Control type="text" placeholder="Enter Video Image URL" />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                            <Form.Control type="text" placeholder="Enter Youtube Video Link" />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose} style={{ fontFamily: "-moz-initial" }}>
                        Cancel
                    </Button>
                    <Button variant="primary" className='btn btn-warning' style={{ fontFamily: "-moz-initial" }}>Upload</Button>
                </Modal.Footer>
            </Modal>

        </>
    )
}
export default Add;
