import React from 'react';
import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

function Add(){
    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
    return(
        <>
        <div className='mt-5 ms-5 d-flex align-item-center'>
        <h5>
            Upload New Videos
        </h5>
        <i class="fa-solid fa-cloud-arrow-up ms-3 mt-2" style={{color:"white"}}></i> 
         </div>
         <Button variant="primary" onClick={handleShow}>
        Launch static backdrop modal
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title style={{fontFamily:"-moz-initial",fontSize:"23px"}}>  <i class="fa-solid fa-film text-warning me-3"></i>Upload Video</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form action=""></form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose} style={{fontFamily:"-moz-initial"}}>
            Cancel
          </Button>
          <Button variant="primary" className='btn btn-warning'style={{fontFamily:"-moz-initial"}}>Upload</Button>
        </Modal.Footer>
      </Modal>
        
        </>
    )
}
export default Add;
